import getPhotoUrl from 'get-photo-url';
import { useState } from 'react'; 
import { db } from '../dexie'


const Gallery = () => {
    const [allPhotos, setAllPhotos] = useState([]);

    const addPhoto = async () => {
        const newPhoto = {
            id: Date.now(),
            url: await getPhotoUrl('#addPhotoInput')
        }

        setAllPhotos([newPhoto, ...allPhotos])
        await db.gallery.put(newPhoto, 'image')
    }

    return (
        <>
            <input type="file" accept='image/*' name="photo" id="addPhotoInput" />
            <label htmlFor="addPhotoInput" onClick={addPhoto}>
                <i className="add-photo-button fas fa-plus-circle"></i>
            </label>

            <section className='gallery'>
                {allPhotos.map(photo => (
                    <div className='item' key={photo.id}>
                        <img src={photo.url} className="item-image" alt="pexel" />
                        <button className='delete-button'>Delete</button>
                    </div>
                ))}
            </section>
        </>
    )
}

export default Gallery;
