import {useState} from 'react'
import profileIcon from '../assets/profileIcon.svg'

const Bio = () => {

    const [userDatails, setUserDatails] = useState({
        'name': 'Williams Williams',
        'about': 'World class software engineer'
    })

    const updateUserDetails = (event) => {
        event.preventDefault()

        setUserDatails({
            name: event.target.nameOfUser.value,
            about: event.target.nameOfUser.value,
        })
    }

    const [valueOfButton, setValueOfButton] = useState('click me')


    const editForm = (
        <form className='edit-bio-form'>
            <input type="text" id="" placeholder="Your name" />
            <input type="text" id="" placeholder="About you" />
            <br />
            <button type='button' className='cancel-button'>Cancel</button>
            <button type='button'>Save</button>
        </form>
    )

    return (
        <section className="bio">
            <div className="profile-photo" role="button" title="Click to edit photo">
                <img src={profileIcon} alt="Profile" />
            </div>

            <div className='profile-info'>
                <p className='name'>{userDatails.name}</p>
                <p className='about'>{userDatails.about}</p>
                <button onClick={() => setValueOfButton('you clicked me')}>{valueOfButton}</button>
                {editForm}
            </div>
        </section>
    )
}

export default Bio
