import { useState } from 'react'; 
import pexels1 from '../assets/pexels1.jpeg'
import pexels2 from '../assets/pexels2.jpeg'
import pexels3 from '../assets/pexels3.jpeg'
import pexels4 from '../assets/pexels4.jpeg'
import pexels5 from '../assets/pexels5.jpeg'

const Gallery = () => {
    const [allPhotos, setAllPhotos] = useState([
        {
            id: 1,
            url: pexels1
        },
        {
            id: 2,
            url: pexels2
        },
        {
            id: 3,
            url: pexels3
        },
        {
            id: 4,
            url: pexels4
        },
        // {
        //     id: 5,
        //     url: pexels5
        // }
    ]);

    return (
        <>
            <input type="file" name="photo" value="" id="addPhotoInput" />
            <label for="addPhotoInput">
                <i className="add-photo-button fas fa-plus-circle"></i>
            </label>

            <section className='gallery'>
                {allPhotos.map(photo => (
                    <div className='item'>
                        <img src={photo.url} className="item-image" alt="pexel" />
                        <button className='delete-button'>Delete</button>
                    </div>
                ))}
            </section>
        </>
    )
}

export default Gallery;
